/*
    Plik:               SPaper.js
    Funkcja:            SKLEP - PAPIER
    Opis:               Strona sklepu wyświetlająca artykuły z kategorii papier.
    Elementy:           Product Query - ComponentDidMount(), 
                        Karta Produktu dla każdego z elementów (ArdCard)
    Przykład użycia:    N/A
    Dodatkowe info:     Query + zwrócenie wyniku w kartach
*/

import React, { useContext, useEffect, useState } from 'react';
import ArtCard from '../../components/ArtCard/ArtCard';
import { CartContext } from '../App';
import axios from 'axios';

const Shoppaper = () => {
    const [Products, setProducts] = useState([]);

    useEffect(() => {
        axios.get(`http://localhost:80/WSB_SELCOR/SERVER/SPaper.php`)
        .then(res => {
            setProducts(res.data);
        })
    }, [])

    let showCards;

    showCards = (
        <div cassName="ProdFlexbox">
            {Products.map((r, index) => {
                return(
                    <ArtCard 
                        imagename={r.p_code}
                        prodid = {r.p_id}
                        name = {r.p_name}
                        shortdesc = {r.p_shortdescription}
                        description = {r.p_description}
                        price={r.p_price}
                        total={this.props.total}
                        key={index}
                    />
                )
            })}
        </div>
    )

    return(
        <h1>SHOP - PAPER ARTICLES</h1>
    )
}

export default Shoppaper;
