import React, { useEffect } from 'react';

const Slider = () => {

    const [SlideNo, setSlideNo] = useState(0);

    const changeSlide = () => {
        setSlideNo(SlideNo + 1);
    }

    setInterval(() => {
        changeSlide();
    }, 1000);

    return(
        <div className="Slider">
            {SlideNo}
        </div>
    )
}

export default Slider;
