import React, { useContext, useState } from 'react';
import './UserBtn.scss';
import Login from '../Login/Login';
import { UserContext } from '../../containers/App';

const UserBtn = (props) => {

    const [User, setUser] = useContext(UserContext);
    const [LogShow, setLogShow] = useState(false);

    // Login pop-up (if user is not logged in)

    let LoginW;

    const loginWindow = () => {
        if (!LogShow) {
            setLogShow(true);
        } else {
            setLogShow(false);
        }
    }

    if (LogShow) {
        LoginW = (
            <div className="LoginW">
                <Login/>
            </div>
        )
    } else {
        LoginW = (
            <div className="LoginW hidden">
                <Login/>
            </div>
        )
    }

    // Acount menu (if user is logged in)

    let AccountMenu;

    const accountMenu = () => {

    }

    // Checking if user is logged in

    let UserInfoBtn;

    if (!User.login) {
        UserInfoBtn = <button className="LogBtn" onClick={loginWindow}>ZALOGUJ</button>
    } else {
        UserInfoBtn = <button className="LogBtn">{User.login}</button>
    }

    return(
        <div className="UserBtnContainer">
            {LoginW}
            {AccountMenu}
            {UserInfoBtn}
        </div>
    )

}

export default UserBtn;
