import React, { useContext, useState } from 'react';
import './UserBtn.scss';
import Login from '../Login/Login';
import AccountMenu from '../AccountMenu/AccountMenu';
import { UserContext } from '../../containers/App';
import { FaRegUserCircle } from 'react-icons/fa';

const UserBtn = (props) => {

    const [User, setUser] = useContext(UserContext);
    const [LogShow, setLogShow] = useState(false);
    const [AccShow, setAccShow] = useState(false);

    console.log(User);

    // Login pop-up (if user is not logged in)

    let windowstyle;
    //let LoginW;

    const loginWindow = () => {
        setLogShow(!LogShow);
    }

    if (LogShow) {
        //LoginW = "LoginW"
    } else {
        windowstyle = {
            visibility: 'hidden',
            opacity: 0,
            transitionDuration: '0.5s'
        }
    }

    // Acount menu (if user is logged in)

    let AccountM;

    const accountMenu = () => {
        setAccShow(!AccShow)
    }

    if (AccShow) {
        AccountM = "LoginW";
    } else {
        AccountM = "LoginW hidden";
    }

    

    // Checking if user is logged in

    let UserInfoBtn;

    if (!User.userinfo.login) {
        UserInfoBtn = <button className="LogBtn" onClick={loginWindow}>ZALOGUJ</button>
    } else {
        UserInfoBtn = <button className="LogBtn AccountBtn" onClick={accountMenu}><FaRegUserCircle size={20} className="UserIcon"/>{User.userinfo.login}</button>
    }

    return(
        <div className="UserBtnContainer">
            <div className="LoginW" style={windowstyle}>
                <Login/>
            </div>
            <div className={AccountM}>
                <AccountMenu/>
            </div>
            {UserInfoBtn}
        </div>
    )

}

export default UserBtn;
