import React, { useEffect, useState } from 'react';
import './Slider.scss';

const Slider = () => {

    const [SlideNo, setSlideNo] = useState(0);
    
    let boxStyle, boxText;
    let img0 = require('../../images/images/APS.jpg');
    let img1 = require('../../images/images/ABI.jpg');
    let img2 = require('../../images/images/KOP.jpg');
    let img3 = require('../../images/images/APP.jpg');

    const changeSlide = () => {
        setSlideNo(SlideNo + 1);
        if(SlideNo > 2) {
            setSlideNo(0);
        }
    }

    useEffect(() => {
        const interval = setInterval(() => {
            changeSlide();
        }, 2000);
        return () => clearInterval(interval);
    })

    switch(SlideNo) {
        case 0: {boxStyle = {backgroundImage: url(img0), transitionDuration: '1s'}; boxText="Artykuły piśmiennicze"}; break;
        case 1: {boxStyle = {backgroundColor: 'green', transitionDuration: '1s'}; boxText="Artykuły biurowe"}; break;
        case 2: {boxStyle = {backgroundColor: 'cornflowerblue', transitionDuration: '1s'}; boxText="Koperty"}; break;
        case 3: {boxStyle = {backgroundColor: 'orange', transitionDuration: '1s'}; boxText="Artykuły papiernicze"}; break;
    }

    /*

    if (SlideNo === 0) {
        boxStyle = {backgroundColor: 'maroon', transitionDuration: '1s'}
    }
    if (SlideNo === 1) {
        boxStyle = {backgroundColor: 'green', transitionDuration: '1s'}
    }

    */

    return(
        <div className="Slider">
            {SlideNo}
            <div className="SlideBox" style={boxStyle}>
                {boxText}
            </div>
        </div>
    )
}

export default Slider;
