import React, { useContext, useState } from 'react';
import './UserBtn.scss';
import Login from '../Login/Login';
import { UserContext } from '../../containers/App';

const UserBtn = (props) => {

    const [User, setUser] = useContext(UserContext);
    const [LogShow, setLogShow] = useState(false);
    const [AccShow, setAccShow] = useState(false);

    // Login pop-up (if user is not logged in)

    let LoginW = "LoginW hidden";

    const loginWindow = (LoginW) => {
        console.log(LoginW);
        if (!LogShow) {
            return(LoginW = "LoginW");
            setLogShow(true);
        } else {
            LoginW = "LoginW hidden"
            setLogShow(false);
        }
    }
/*
    if (LogShow) {
        LoginW = "LoginW"
    } else {
        LoginW = "LoginW hidden"
    }
*/
    // Acount menu (if user is logged in)

    let AccountMenu;

    const accountMenu = () => {
        if (!AccShow) {
            setAccShow(true);
        } else {
            setAccShow(false);
        }
    }

    

    // Checking if user is logged in

    let UserInfoBtn;

    if (!User.login) {
        UserInfoBtn = <button className="LogBtn" onClick={loginWindow.bind(this, LoginW)}>ZALOGUJ</button>
    } else {
        UserInfoBtn = <button className="LogBtn" onClick={accountMenu}>{User.login}</button>
    }

    return(
        <div className="UserBtnContainer">
            <div className={LoginW}>
                <Login/>
            </div>
            {AccountMenu}
            {UserInfoBtn}
        </div>
    )

}

export default UserBtn;
