import React, { useEffect, useContext } from 'react';
import './SearchResult.scss';
import ArtCard from '../../components/ArtCard/ArtCard';
import Axios from 'axios';
import { ServerPath } from '../../containers/App';
import { UserContext } from '../App';

const SearchResult = (props) => {
    const [User, setUser] = useContext(UserContext);

    console.log(props.location.data);

    const SendQuery = (data) => {
        Axios.post(ServerPath + 'Search.php',
        data)
        .then(function (res) {
            setUser({...User, searchmemo: res.data});
        })
    }

    useEffect(() => {
        SendQuery(props.location.data);
    }, [])

    let showCards;

    if (User.searchmemo.length === 0) {

    } else {
        showCards = (
            <div className="ProdFlexbox">
                {User.searchmemo.map((r, index) => {
                    return(
                        <ArtCard 
                            imagename={r.p_code}
                            prodid = {r.p_id}
                            name = {r.p_name}
                            shortdesc = {r.p_shortdescription}
                            description = {r.p_description}
                            price={r.p_price}
                            key={index}
                        />
                    )
                })}
            </div>
        )
    }

    return(
        <div>
            {showCards}
        </div>
    )
}

export default SearchResult;
