import React, { useContext, useEffect, useState } from 'react';
import './Account.scss';
import { UserContext, ServerPath } from '../../containers/App';
import TitleBar from '../../components/TitleBar/TitleBar';
import Axios from 'axios';
import Cookies from 'js-cookie';
import { FaRegUserCircle } from 'react-icons/fa';

const Account = () => {
    const [User, setUser] = useContext(UserContext);
    const [AccountInfo, setAccountInfo] = useState([]);
    const [AccountFound, setAccountFound] = useState(false);
    //let UserId = Cookies.get('psid');
    //let DecodedUserId = decodeURIComponent(UserId);
    let SessionId = Cookies.get('pssession');
    //let DecodedUserName = decodeURIComponent(UserName);
    //const [UserData, setUserData] = useState({
    //    id: DecodedUserId,
    //    login: DecodedUserName
    //});

    useEffect(() => {
        if (SessionId !== null) {
            Axios.post(ServerPath   + 'UserAccount.php', SessionId)
            .then(res => {
                if(res.data.length > 0) {
                    setAccountInfo(res.data);
                    console.log(res.data);
                    setAccountFound(true);
                }
            })
        }
    }, [])

    console.log(AccountFound);

    let AccountData;
    let login, mail, address, name, surname, fullname, street, streetno, flatno, city, zip, nip, regon;
    let AccountType;

    if (AccountFound) {
        AccountInfo.map((r, index) => {
            console.log(r.cc_name);
            if (r.cc_name == null) {
                setUser({...User, userinfo: {
                    AccountType: "indywidualne",
                    login: r.u_login,
                    mail: r.u_mail,
                    name: r.ci_name,
                    surname: r.ci_surname,
                    fullname: r.ci_name + " " + r.ci_surname,
                    street: r.ci_street,
                    streetno: r.ci_number,
                    flatno: r.ci_number_flat,
                    city: r.ci_city,
                    zip: r.ci_zip
                }})

            } else {
                AccountType = "firmowe";
                login = r.u_login;
                mail = r.u_mail;
                name = r.cc_name;
                fullname = r.cc_name;
                street = r.cc_street;
                streetno = r.cc_number;
                flatno = r.cc_number_flat
                city = r.cc_city;
                zip = r.cc_zip;
                nip = r.cc_NIP;
                regon = r.cc_REGON;
            }
            //key = {index};
        })

        if (flatno) {
            address = streetno + "/" + flatno;
        } else {
            address = streetno;
        }

        if (AccountType === "indywidualne") {
            AccountData = (
                <div className="AccountData">
                    <div className="AccountNameContainer">
                        <div className="AccountName">
                            <FaRegUserCircle size={50}/>
                            <h1>{fullname}</h1>
                        </div>
                        <div className="AccountType">Konto {User.userinfo.AccountType}</div>
                    </div>
                    <div className="AccountInfo">
                        <h3>DANE OSOBOWE</h3>
                        <div className="AccountData">
                            <p>Login: {User.userinfo.login}</p>
                            <p>E-mail: {User.userinfo.mail}</p>
                            <p>ImiÄ™: {User.userinfo.name}</p>
                            <p>Nazwisko: {User.userinfo.surname}</p>
                        </div>
                        <hr className="separator"/>
                        <h3>ADRES</h3>
                        <div>
                            <p>{User.userinfo.street} {User.userinfo.address}</p>
                            <p>{User.userinfo.zip} {User.userinfo.city}</p>
                        </div>
                    </div>
                </div>
            )
        }
        if (AccountType === "firmowe") {
            
        }
    } else {
        AccountData = <p>Brak danych</p>
    }


    return(
        <div className="SiteContainer">
            <TitleBar title="Dane konta"/>
            {AccountData}
        </div>
    )
}

export default Account;
