/*
    Plik:               Login.js
    Funkcja:            STRONA Z FORMULARZEM REJESTRACYJNYM
    Opis:               Formularz rejestracyjny dla osób chcących założyć konto
    Elementy:           Formularz rejestracyjny
    Przykład użycia:    N/A
    Dodatkowe info:     Walidacja danych, dodawanie nowego usera
*/

import React, { useState, useContext } from 'react';
import './LoginSite.scss'
import Axios from 'axios';
import { UserContext, ServerPath } from '../App';
import { useHistory } from 'react-router-dom';

const LoginSite = (props) => {

    const [ErrorInfo, setErrorInfo] = useState();
    const [Login, setLogin] = useState();
    const [User, setUser] = useContext(UserContext);

    const handleLogin = (event) => {
        if (event.keyCode === 13) {
            submitCredentials();
        } else {
            setLogin({...Login, user: event.target.value});
        }
    }

    const handlePassword = (event) => {
        if (event.keyCode === 13) {
            submitCredentials();
        } else {
            setLogin({...Login, password: event.target.value});
        }
    }

    const submitCredentials = () => {
        Axios.post(ServerPath + 'extLogin.php', Login)
        .then(res => {
            if(res.data.mail) {

            } else {
                let errordata = (
                    <div className="ErrorInfo">
                        {res.data.map((r, index) => {
                            return(
                                <p key={index}>{r}</p>
                            )
                        })}
                    </div>
                
                )
                setErrorInfo(errordata);
            }
            if(res.data.length > 0) {
                //setAccountInfo(res.data);
                console.log(res.data);
                res.data.map((r, index) => {
                    setUser((prevState) => ({...prevState, accinfo: {
                        mail: r.u_mail,
                        cname: r.cc_name,
                        ccity: r.cc_city,
                        cstreet: r.cc_street,
                        chouse: r.cc_number,
                        cflat: r.cc_number_flat,
                        czip: r.cc_zip,
                        cnip: r.cc_NIP,
                        cregon: r.cc_REGON,
                        iname: r.ci_name,
                        isurname: r.ci_surname,
                        icity: r.ci_city,
                        istreet: r.ci_street,
                        ihouse: r.ci_number,
                        iflat: r.ci_number_flat,
                        izip: r.ci_zip
                    }}));
                })
            }
        })
    }

    return(
        <div className="SiteContainer">
            <div className = "AuthWindow">
                <h3>Dostęp do informacji: {props.feature} wymaga dodatkowego uwierzytelnienia.</h3>
                <p>Proszę zaloguj się ponownie</p>
                <label for="mail"/>
                <input type="text" name="mail" onChange={handleLogin}></input><br/>
                <label for="password"/>
                <input type="text" name="password" onChange={handlePassword}></input><br/>
                <button type="submit">ZALOGUJ</button>
                <div>{ErrorInfo}</div>
            </div>
        </div>
    )
}

export default LoginSite;
