import React, { useContext, useState } from 'react';
import './UserBtn.scss';
import { UserContext } from '../../containers/App';

const UserBtn = (props) => {

    const [User, setUser] = useContext(UserContext);
    const [LogShow, setLogShow] = useState(false);

    let LoginW;

    const loginWindow = () => {
        if (!LogShow) {
            setLogShow(true);
        } else {
            setLogShow(false);

        }
    }

    if (LogShow) {
        LoginW = (
            <div className="LoginW">
                <Login/>
            </div>
        )
    } else {
        LoginW = (
            <div className="LoginW hidden">
                <Login/>
            </div>
        )
    }

    let UserInfoBtn;

    if (User.name === null) {
        UserInfoBtn = <button className="LogBtn" onClick={loginWindow}>ZALOGUJ</button>
    } else {
        UserInfoBtn = <button className="LogBtn">{User.login}</button>
    }

}

export default UserBtn;
