import React, { useState } from 'react';
import './TitleBar.scss';

const titlebar = (props) => {

    BarStyle = useState({color: black});

    let firstLetter = props.title.charAt(0);
    let titleRest = (props.title.substring(1, props.title.length)).toUpperCase();
    console.log(firstLetter);
    console.log(titleRest);
    return(
        <div class="TitleBar">
            <h1 className="FirstLetter">{firstLetter}</h1><h1 className="TitleRest">{titleRest}</h1>
        </div>
    )
}

export default titlebar;
