    useEffect(() => {
        if (SessionId !== null) {
            //console.log(User.accinfo.length);
            if (User.accinfo.length == null) {
                Axios.post(ServerPath   + 'UserAccount.php', SessionId)
                .then(res => {
                    if(res.data.length > 0) {
                        //setAccountInfo(res.data);
                        console.log(res.data);
                        setAccountFound(true);
                        setUser((prevState) => ({...prevState, accinfo: res.data}));
                    }
                })
            } else {
                console.log("We still have it");
            }
        }
    }, [])

    let AccountData;

    if (AccountFound) {
        console.log(User.userinfo.uid)
    }
