import React, { useEffect, useContext, useState } from 'react';
import './SearchResult.scss';
import ArtCard from '../../components/ArtCard/ArtCard';
import Cookies from 'js-cookie';
import Axios from 'axios';
import { UserContext, ServerPath } from '../App';
import TitleBar from '../../components/TitleBar/TitleBar';

const SearchResult = (props) => {
    //const [User, setUser] = useContext(UserContext);
    //const [ReadCookie, setReadCookie] = useState();
    const [SearchHook, setSearchHook] = useState([]);

    let SearchingCookie = Cookies.get('pssearching');
    let DecodedCookie = decodeURIComponent(SearchingCookie);
    let InfoTxt = "Wyniki wyszukiwania dla frazy: " + DecodedCookie;

    let DataQuery;
    let showCards;

    console.log(DecodedCookie);

    useEffect(() => {
        if (typeof(props.location.searchProps) !== 'undefined') {
            DataQuery = props.location.searchProps;
            console.log("props: " + props.location.searchProps)
        } else {
            DataQuery = DecodedCookie;
        }

        if (typeof(DecodedCookie) !== null) {
            Axios.post(ServerPath + 'Search.php',
            DataQuery)
            .then(function (res) {
                console.log(res.data);
                setSearchHook([...SearchHook, res.data]);
            })
            
            /*
            showCards = (
                <div className="ProdFlexbox">
                    {SearchHook.map((r, index) => {
                        return(
                            <ArtCard 
                                imagename={r.p_code}
                                prodid = {r.p_id}
                                name = {r.p_name}
                                shortdesc = {r.p_shortdescription}
                                description = {r.p_description}
                                price={r.p_price}
                                key={index}
                            />
                        )
                    })}
                </div>
            )
            */
        } else {
            showCards = <h4>Brak wyników</h4>
        }
    }, [])

    //if(Object.values(SearchHook[0]).length === 0) 

    if(typeof(SearchHook[0]) === 'undefined') {
        console.log("it's empty");
    } else {
        console.log(SearchHook);
    }


/*
    if (typeof(SearchHook) === null) {
        if (DecodedCookie !== 'undefined') {
            Axios.post(ServerPath + 'Search.php',
            DecodedCookie)
            .then(function (res) {
                setSearchHook([...SearchHook, res.data]);
            })
            showCards = (
                <div className="ProdFlexbox">
                    {SearchHook.map((r, index) => {
                        return(
                            <ArtCard 
                                imagename={r.p_code}
                                prodid = {r.p_id}
                                name = {r.p_name}
                                shortdesc = {r.p_shortdescription}
                                description = {r.p_description}
                                price={r.p_price}
                                key={index}
                            />
                        )
                    })}
                </div>
            )
        } else {
            showCards = <h4>Brak wyników</h4>
        }
    } else {
        showCards = (
            <div className="ProdFlexbox">
                {SearchHook.map((r, index) => {
                    return(
                        <ArtCard 
                            imagename={r.p_code}
                            prodid = {r.p_id}
                            name = {r.p_name}
                            shortdesc = {r.p_shortdescription}
                            description = {r.p_description}
                            price={r.p_price}
                            key={index}
                        />
                    )
                })}
            </div>
        )
    }
*/
    return(
        <div>
            <TitleBar title={InfoTxt}/>
            {showCards}
        </div>
    )
}

export default SearchResult;
