/*
    Plik:               Cart.js
    Funkcja:            STRONA PODSUMOWANIA ZAKUPÓW - KOSZYK
    Opis:               Strona wyświetlająca dodane artykuły do koszyka i pokazująca sumę należności za zakupy
    Elementy:           Nawigacja górna (Navbar), 
                        Logo (PS Logo), 
                        Searching Bar (SearchBar), 
                        Przycisk Zaloguj (LogBtn => Login.js), 
                        Przycisk Koszyka (CartBtn => Cart.js), 
                        Suma zakupów {props.totalprice} - wyświetlane w przycisku Koszyk
                        Nawigacja dolna - sklep (ArtNav)
    Przykład użycia:    Komponent bazowy wyświetlany w App.js - <Header totalprice={suma} />
    Dodatkowe info:     <Navlink> - list przekierowujący do odpowiedniego kontenera w routingu - routing w pliku głównym App.js
*/

import React, { useContext, useEffect } from 'react';
import App, { CartContext } from '../App';
import CartItem from '../../components/CartItem/CartItem';
import './Cart.scss';

const SCart = (props) => {

    const [initCart, Cart] = useContext(CartContext);

    const increaseQuantity = (index, name, desc, price, q, imagename) => {
        /*
        const newCart = {...Cart[itemIndex]}; 
        if (initCart.quantity < 100) {
            Cart({
                quantity: quantity+1
            })
        }
        */
        console.log(initCart[index].quantity)
       // const itemIndex = Cart.findIndex(i => {
       //     return i.id === index;
       // })
       newCart = [...initCart];
       newCart[index].quantity = 7;
        /*
        Cart(initCart => ({
            id: initCart.index,
            name: initCart.name,
            price: initCart.price,
            quantity: initCart.quantity+1,
            prodtotal: initCart.quantity+initCart.price,
            imagename: initCart.imagename
        }))
        */
       
    }
    
    const decreaseQuantity = (index) => {
        /*
        if(initCart.quantity > 0) {
            Cart({
                quantity: quantity-1
            })
        }
        */
    }

    const handleQuantity = (event, index) => {
        Cart({
            quantity: parseInt(event.target.value)
        })
    }

    let ShowCartItems;

    if (initCart.length === 0) {
        ShowCartItems = "Twój koszyk jest pusty"
    } else {
        ShowCartItems = (
            <div className="CartItems">
                {initCart.map((r, index) => {
                    return(
                        <CartItem 
                            name={r.name}
                            desc={r.desc}
                            price={r.price}
                            quantity={r.quantity}
                            prodtotal={r.prodtotal}
                            imagename={r.imagename}
                            key={index}
                            increaseq={increaseQuantity.bind(this, index)}
                            //handleq={handleQuantity.bind(this, index)}
                            //decreaseq={decreaseQuantity.bind(this, index)}
                        />
                    )
                })}
            </div>
        )
    }

    return(
        <div>
            <h1>CART</h1>
            <div className="CartList">
                <h4>TWÓJ KOSZYK</h4>
                {ShowCartItems}
            </div>
            <div className="CartSummary">
                <h4>PODSUMOWANIE</h4>
            </div>
        </div>
    )
}

export default SCart;
