import React, { useState } from 'react';

const ChangePassword = (props) => {

    const [Pass, setPass] = useState();

    const handlePassword = (option, event) => {
        switch(option) {
            case 0: setPass({...Pass, oldpass: event.target.value});
            case 1: setPass({...Pass, newpass0: event.target.value});
            case 2: setPass({...Pass, newpass1: event.target.value});
        }
    }

    console.log(Pass);

    return(
        <div className="AccountInfo">
            <div className="ChangePanel">
                <h3>ZMIANA HASŁA</h3>
                <label htmlFor="oldpass">Stare hasło</label>
                <input name="oldpass" onChange={handlePassword.bind(0)} onKeyDown={handlePassword.bind(0)}/>
                <label htmlFor="oldpass">Nowe hasło</label>
                <input name="oldpass" onChange={handlePassword.bind(1)} onKeyDown={handlePassword.bind(1)}/>
                <label htmlFor="oldpass">Powtórz nowe hasło</label>
                <input name="oldpass" onChange={handlePassword.bind(2)} onKeyDown={handlePassword.bind(2)}/>
                <hr className="separator"/>
            </div>
            <div className="AccountPanel">
                <button onClick={props.submitdatachange}>ZMIEŃ HASŁO</button>
                <button onClick={props.changetab}>POWRÓT</button>
            </div>
        </div>
    )
}

export default ChangePassword;
