import React from 'react';
import ImageList from "../ImageList/ImageList";

const imagelist = [
    {id: 1, src: './ARTY13190.jpg'},
    {id: 2, src: './ARTY13190.jpg'},
    {id: 3, src: './ARTY22670.jpg'}
]

const artcard = (props) => {

    let ImgList

    ImgList = (
        <div>
            {ImageList.map((imgrec, index) => {
            if (props.imagename == imgrec.id) {
                return(
                    <img src={imgrec.src} alt={imgrec.id}/>
                );
            }
            })};
        </div>
    )

    return(
        <div className="artCard">
            {ImgList}
            <h4>{props.name}</h4>
            <p>{props.description}</p>
            <p>{props.price}</p>
        </div>
    );
}

export default artcard;
