import React, { useState, useContext } from 'react';
import { CartContext, TotalContext } from '../../containers/App';
import './ArtDetails.scss';
import carticon from '../../images/icons/black_cart.png';

const Artdetails = (props) => {

    const [initCart, Cart] = useContext(CartContext);                               // stan globalny - stan koszyka
    const [initTotal, Total] = useContext(TotalContext);                            // stan globalny - suma do zapłaty
    const [counter, setCounter] = useState({quantity: 0})              // stan lokalny - wyświetla ilość produktu
    const [totalPrice, setTotalPrice] = useState({totalprice: props.prodtotal})     // stan lokalny - wyświetla sumę do zapłaty

    const Image = require('../../images/images/' + 'CC3' + '.png');       // Ścieżka do zdjęcia

    let ProdImgStyle;
    ProdImgStyle = {                                                                // Stylizacja zdjęcia - zdjęcie jako tło karty
        backgroundImage: "url(" + Image + ")",
        backgroundColor: 'white',
    }

    const increaseQuantity = (index) => {
        let newCart = [...initCart];
        newCart[index].quantity = newCart[index].quantity+1;
        newCart[index].prodtotal = parseFloat(newCart[index].prodtotal) + parseFloat(newCart[index].price);
        
        setCounter({
            quantity: newCart[index].quantity
        })
        setTotalPrice({
            totalprice: newCart[index].prodtotal
        })
        Total({
            total: parseFloat(initTotal.total) + parseFloat(newCart[index].price)
        })
    }

    const decreaseQuantity = (index) => {
        if (counter.quantity > 1) {
            let newCart = [...initCart];
            newCart[index].quantity = newCart[index].quantity-1;
            newCart[index].prodtotal = parseFloat(newCart[index].prodtotal) - parseFloat(newCart[index].price);

            setCounter({
                quantity: newCart[index].quantity
            })
            setTotalPrice({
                totalprice: newCart[index].prodtotal
            })
            Total({
                total: parseFloat(initTotal.total) - parseFloat(newCart[index].price)
            })
        }
    }

    const handleQuantity = (event) => {
        let regexp = /^[0-9\b]+$/
        let input = event.target.value
        if (regexp.test(input)) {
        setCounter({
            quantity: parseInt(input)
        })
        } else {
            document.getElementById("qInput").value = 0;
        }
    }

    const addProd = (prodid, name, desc, q, price, imagename) => {
        let check = 0;
        let idToChange;
        let orSum = parseFloat((q*price));

        initCart.map((r, index) => {
            if (r.prodid === prodid) {
                check = 1
                idToChange = parseInt(index);
            }
        })

        if (q > 0) {
            if (check === 0) {
                Cart(prevCart => ([...initCart, {
                    id: initCart.length, 
                    prodid: prodid,
                    name: name, 
                    desc: desc,
                    price: parseFloat(price),
                    quantity: q, 
                    prodtotal: q*price,
                    imagename: imagename
                }]))
            } else {
                let newCart = [...initCart];
                newCart[idToChange].quantity = newCart[idToChange].quantity+q;
                newCart[idToChange].prodtotal = newCart[idToChange].price*newCart[idToChange].quantity;
            }
            Total({
                total: parseFloat(initTotal.total+orSum)
            })
            setCounter({
                quantity: 0
            })
        }
    }

    console.log(props.location.artProps)
    return(
        <div className="ArtDetails">
            <div className="ArtDContainer">
                <div className="ArtImgContainer" style={ProdImgStyle}>
                </div>
                <div className="ArtDCard">
                    <div className="ArtDMain">
                        <h4 className="ArtDTitle">Produkt</h4>
                        <p className="ArtDShortDesc">Czarny</p>
                    </div>
                    <div className="ArtDDesc">
                        <p>Dehi dhi3 jehiuhf4i jheihiorheo frhirhiohr mfrhri.</p>
                    </div>
                    <div className="ArtDPrice">
                        <div>
                            Cena netto:
                            <p className="NetPrice">3.68</p>
                        </div>
                        <div className="GrossPriceContainer">
                            Cena brutto: 
                            <p className="GrossPrice">5.55</p>
                        </div>
                    </div>
                    <div className="ArtDOrderBar">
                        <div className="ArtDOrderBar">
                            <button className="ArtDqBtn" onClick={increaseQuantity}>+</button>
                            <input className="ArtDqInput" id="qInput" type="text" value={counter.quantity} onChange={handleQuantity}/>
                            <button className="ArtDqBtn" onClick={decreaseQuantity}>-</button>
                        </div>
                        <button className="ArtDOrderBtn" onClick={addProd.bind(this, props.prodid, props.name, props.shortdesc, counter.quantity, props.price, props.imagename)}><img className="carticonBtn" src={carticon} alt="cart"/>DO KOSZYKA</button>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Artdetails;

