import React from 'react';
import "./ArtCard.scss";

const artcard = (props) => {

    const Image = require('../../images/images/' + props.imagename + '.jpg');

    /*
    let ImgList

    ImgList = (
        <div>
            {ImageList.map((imgrec, index) => {
            if (props.imagename == imgrec.id) {
                return(
                    <img src={imgrec.src} alt={imgrec.id}/>
                );
            }
            })};
        </div>
    )
    */

    return(
        <div className="artCard">
            <img className="ProdImg" src={Image} alt={props.imagename}/>
            <h4>{props.name}</h4>
            <p>{props.description}</p>
            <p>{props.price}</p>
        </div>
    );
}

export default artcard;
