/*
    Plik:               Login.js
    Funkcja:            STRONA Z FORMULARZEM REJESTRACYJNYM
    Opis:               Formularz rejestracyjny dla osób chcących założyć konto
    Elementy:           Formularz rejestracyjny
    Przykład użycia:    N/A
    Dodatkowe info:     Walidacja danych, dodawanie nowego usera
*/

import React, { useState, useContext, useEffect } from 'react';
import './ExtLoginSite.scss'
import Axios from 'axios';
import { UserContext, ServerPath } from '../App';
import { useHistory } from 'react-router-dom';
import { MdErrorOutline } from "react-icons/md";
import Cookies from 'js-cookie';

const LoginSite = (props) => {

    const [ErrorInfo, setErrorInfo] = useState();
    const [LogShow, setLogShow] = useState(false);      // Show loggin button
    const [LoginData, setLoginData] = useState();       // Temporary state for sending login data
    const [User, setUser] = useContext(UserContext);

    const handleUsername = (event) => {
        if (event.keyCode === 13) {
            handleLogin();
        } else {
            setLogin({...Login, user: event.target.value});
        }
    }

    const handlePassword = (event) => {
        if (event.keyCode === 13) {
            handleLogin();
        } else {
            setLogin({...Login, password: event.target.value, userid: User.userinfo.login});
        }
    }

    const History = useHistory();

    const handleLogin = () => {
        Axios.post(ServerPath + "Login.php", LoginData)
        .then(function(res) {
            //console.log(res.data);
            
            if (res.data.uid) {
                //console.log(res.data);
                setUser({...User, userinfo: res.data});
                Cookies.set('pssession', res.data.sessionid);
                setLogShow(false);
                History.push('/');
            } else {
                let errordata = (
                    <div className="ErrorInfo">
                        {res.data.map((r, index) => {
                            return(
                                <p key={index}>{r}</p>
                            )
                        })}
                    </div>
                
                )
                setErrorInfo(errordata);
            }
        })
        //let sessionid = Cookies.get('pssession');
        //Axios.post(ServerPath + "Session.php", sessionid)
        //.then(function(res) {
            //console.log(User);
        //    console.log(res.data);
        //})
    }

    console.log(Login);

    return(
        <div className = "AuthWindow">
            <MdErrorOutline size={70}/>
            <h3>Dostęp do informacji: {props.feature}<br/>wymaga dodatkowego uwierzytelnienia.</h3>
            <p>Proszę zaloguj się ponownie</p>
            <label htmlFor="mail">Nazwa użytkownika</label>
            <input type="text" name="mail" onChange={handleLogin}></input><br/>
            <label htmlFor="password">Hasło</label>
            <input type="text" name="password" onChange={handlePassword}></input><br/>
            <button type="submit" onClick={submitCredentials}>ZALOGUJ</button>
            <div>{ErrorInfo}</div>
        </div>
    )
}

export default LoginSite;
