        ci_name: '',
        ci_surname: '',
        ci_street: '',
        ci_number:'',
        ci_number_flat:'',
        ci_zip: '',
        ci_city:''
        */
    });
    const [ControlMenu, setControlMenu] = useState({step1: true, step2: false, step3: false})

    /*
    const handleSubmit = async (event) => {
        error.preventDefault()
        console.log('form submitted', form)
    }
    */

    const handleFields = (option) => {

    }

    const switchSite = (option) => {
        switch(option) {
            case 0: {
                        setControlMenu({step1: true, step2: false, step3: false});
                    }; break;
            case 1: {setControlMenu({step1: false, step2: true, step3: false})}; break;
            case 2: {setControlMenu({step1: false, step2: false, step3: true})}; break;
        }
    }


    let addstyle = {
        display: 'none'
    }

    return(
        
        <div className="RegIndividual">
            <Titlebar title="Rejestracja konta indywidualnego"/>
            <div className="RegMilestones">
                <div>1</div><div>2</div><div>3</div>
            </div>
            <div className="RegMain">
                <RegAccMain
                    next={switchSite.bind(this, 1)}
                />
            </div>
            <div className="MRegForm">

            </div>

        </div>
    )
}

export default RegIndividual;

