import React, { useContext, useState } from 'react';
import './UserBtn.scss';
import Login from '../Login/Login';
import { UserContext } from '../../containers/App';

const UserBtn = (props) => {

    const [User, setUser] = useContext(UserContext);
    const [LogShow, setLogShow] = useState(false);
    const [AccShow, setAccShow] = useState(false);

    // Login pop-up (if user is not logged in)

    let LoginW;

    const loginWindow = () => {
        setLogShow(!LogShow);
    }

    if (LogShow) {
        LoginW = "LoginW"
    } else {
        LoginW = "LoginW hidden"
    }

    // Acount menu (if user is logged in)

    let AccountMenu;

    const accountMenu = () => {
        setLogShow(!AccShow)
    }

    if (AccShow) {
        AccountMenu = "AccountMenu";
    } else {
        AccountMenu = "AccountMenu hidden";
    }

    

    // Checking if user is logged in

    let UserInfoBtn;

    if (!User.login) {
        UserInfoBtn = <button className="LogBtn" onClick={loginWindow}>ZALOGUJ</button>
    } else {
        UserInfoBtn = <button className="LogBtn" onClick={accountMenu}>{User.login}</button>
    }

    return(
        <div className="UserBtnContainer">
            <div className={LoginW}>
                <Login/>
            </div>
            {AccountMenu}
            {UserInfoBtn}
        </div>
    )

}

export default UserBtn;
