import React, { useContext, useEffect, useState } from 'react';
import './Account.scss';
import { UserContext, ServerPath } from '../../containers/App';
import TitleBar from '../../components/TitleBar/TitleBar';
import Axios from 'axios';
import Cookies from 'js-cookie';

const Account = () => {
    const [User, setUser] = useContext(UserContext);
    const [UserData, setUserData] = useState();

    useEffect(() => {
        let UserId = Cookies.get('psid');
        let DecodedUserId = decodeURIComponent(UserId);
        let UserName = Cookies.get('psname');
        let DecodedUserName = decodeURIComponent(UserName);
        if (DecodedUserId !== 'undefined' && DecodedUserName !== 'undefined') {
            setUserData({
                    id: DecodedUserId,
                    login: DecodedUserName
            })
        }

        Axios.post(ServerPath + 'UserAccount.php', User.userinfo)
        .then(function(res) {
            console.log(res.data);
        })
    }, [])

    console.log(UserData);

    return(
        <div className="SiteContainer">
            <TitleBar title="Dane konta"/>

            <p>ImiÄ™: {User.name}</p>
            <p>Nazwisko: {User.surname}</p>
        </div>
    )
}

export default Account;
