import React, { useState, useContext } from 'react';
import App, { CartContext, TotalContext } from '../../containers/App';
import './CartItem.scss';

const CartItem = (props) => {

    const [initCart, Cart] = useContext(CartContext);
    const [initTotal, Total] = useContext(TotalContext);

    const Image = require('../../images/images/' + props.imagename + '.png');
    let prodtotal = parseFloat(props.prodtotal).toFixed(2);

    const increaseQuantity = () => {
        if (props.quantity < 100) {
            Cart({
                quantity: quantity+1
            })
        }
    }
    
    const decreaseQuantity = () => {
        if(props.quantity > 0) {
            setCounter({
                quantity: quantity-1
            })
        }
    }

    const handleQuantity = (event) => {
        setCounter({
            quantity: parseInt(event.target.value)
        })
    }

    return(
        <div className="CartItem">
            <img className="CartItemImg" src={Image} alt="prodimg"/>
            <div className="CartItemContainer">
                <h4 className="CartItemName">{props.name} - {props.desc}</h4>
                <div className="CartItemPrice">
                    <div>
                        <button className="qBtn" onClick={increaseQuantity}>+</button>
                        <input type="text" value={props.quantity} onChange={handleQuantity}/>
                        <button className="qBtn" onClick={decreaseQuantity}>-</button>
                        <p>{prodtotal}</p>
                    </div>
                    <div>{props.price}</div>
                </div>
            </div>
        </div>
    )
}

export default CartItem;
