import React, { useState } from 'react';
import './Login.scss'
import { NavLink, useHistory } from 'react-router-dom';
import Axios from 'axios';
import { ServerPath } from '../../containers/App';

const Login = () => {
/*
    const [LoginData, setLoginData] = useState({
        userdata: [
            {user: ''},
            {password: ''}
        ]
    });
*/

    const [LoginData, setLoginData] = useState({
        user: '', password: ''
    })

    const handleUsername = (event) => {
        setLoginData({...LoginData, userdata: [{user: event.target.value}]});
    }

    const handlePassword = (event) => {
        setLoginData({...LoginData, password: event.target.value});
    }

    let history = useHistory();

    const handleLogin = () => {
        console.log(LoginData.user + "/" + LoginData.password);
        console.log(LoginData.userdata);
        Axios.post(ServerPath + "Login.php", LoginData.userdata)
        .then(function(res) {
            console.log(res.data);
            history.push({
                pathname: '/'
            });
        })
    }

    return(
        <div className="Login">
            <label htmlFor="login">Nazwa użytkownika:</label>
            <input type="text" onChange={handleUsername} name="login"/>
            <label htmlFor="pass">Hasło:</label>
            <input type="password" onChange={handlePassword} name="pass"/>
            <button className="LoginBtn" onClick={handleLogin}>ZALOGUJ</button>
            <p>Nie masz konta?</p>
            <NavLink to="/regform" className="RegBtnContainer">
                <button className="RegBtn">ZAREJESTRUJ SIĘ</button>
            </NavLink>
        </div>
    )
}

export default Login;
