import React, { useState, useContext } from 'react';
import App, { CartContext } from '../../containers/App';
import './CartItem.scss';

const CartItem = (props) => {

    const Image = require('../../images/images/' + props.imagename + '.png');
    let prodtotal = parseFloat(props.prodtotal).toFixed(2);

    return(
        <div className="CartItem">
            <img className="CartItemImg" src={Image} alt="prodimg"/>
            <div className="CartItemContainer">
                <h4 className="CartItemName">{props.name} - {props.desc}</h4>
                <div className="CartItemPrice">
                    <div>
                        {props.quantity} / {prodtotal}
                    </div>
                    <div>{props.price}</div>
                </div>
            </div>
        </div>
    )
}

export default CartItem;
