import React, { useContext, useEffect, useState } from 'react';
import './Account.scss';
import { UserContext, ServerPath } from '../../containers/App';
import { useHistory, NavLink } from 'react-router-dom';
import TitleBar from '../../components/TitleBar/TitleBar';
import Axios from 'axios';
import Cookies from 'js-cookie';
import { FaRegUserCircle } from 'react-icons/fa';
import ShowAccountData from '../../components/ShowAccountData/ShowAccountData';
import EditAccountData from '../../components/EditAccountData/EditAccountData';

const Account = () => {
    const [User, setUser] = useContext(UserContext);
    const [AccountInfo, setAccountInfo] = useState([]);
    const [AccountFound, setAccountFound] = useState(false);
    const [UserLogged, setUserLogged] = useState(false);
    const [ACDisp, setACDisp] = useState(true);
    const [EADisp, setEADisp] = useState(false);
    const [CPDisp, setCPDisp] = useState(false);
    //let UserId = Cookies.get('psid');
    //let DecodedUserId = decodeURIComponent(UserId);
    let SessionId = Cookies.get('pssession');
    let ExtSession = Cookies.get('psacc');
    //let DecodedUserName = decodeURIComponent(UserName);
    //const [UserData, setUserData] = useState({
    //    id: DecodedUserId,
    //    login: DecodedUserName
    //});
    let History = useHistory();

    console.log(User.usercontrol);

    useEffect(() => {
        if (!ExtSession) {
            History.push('/extlogin');
        } else {
            if (SessionId !== null) {
                console.log(User.accinfo);
                if (!User.acccontrol) {
                    console.log("loading...");
                    Axios.post(ServerPath   + 'UserAccount.php', ExtSession)
                    .then(res => {
                        if(res.data.length > 0) {
                            //setAccountInfo(res.data);
                            res.data.map((r, index) => {
                                setUser((prevState) => ({...prevState, accinfo: {
                                    mail: r.u_mail,
                                    cname: r.cc_name,
                                    ccity: r.cc_city,
                                    cstreet: r.cc_street,
                                    chouse: r.cc_number,
                                    cflat: r.cc_number_flat,
                                    czip: r.cc_zip,
                                    cnip: r.cc_NIP,
                                    cregon: r.cc_REGON,
                                    iname: r.ci_name,
                                    isurname: r.ci_surname,
                                    icity: r.ci_city,
                                    istreet: r.ci_street,
                                    ihouse: r.ci_number,
                                    iflat: r.ci_number_flat,
                                    izip: r.ci_zip
                                },
                                    acccontrol: true
                                }));
                            })
                            //console.log(res.data);
                            setAccountFound(true);
                        }
                    })
                } else {
                    console.log("We still have it");
                    setAccountFound(true);
                }
            }
        }
    }, [])

    let AccountData, AccFormData, AccountType, address, fullname, zip1, zip2;

    if (AccountFound) {
        if (User.accinfo.cname === null) {
            AccountType = "indywidualne";
            fullname = User.accinfo.iname + " " + User.accinfo.isurname;
            address = User.accinfo.istreet + " " + User.accinfo.ihouse + "/" + User.accinfo.iflat;
            zip1 = User.accinfo.izip.substring(0,2);
        } else {
            AccountType = "firmowe";
            fullname = User.accinfo.cname;
            address = User.accinfo.cstreet + " " + User.accinfo.chouse + "/" + User.accinfo.cflat;
            zip1 = User.accinfo.czip.substring(0,2);
        }
    } else if (!UserLogged) {
        AccountData = (
            <div>
                Nie wykryto zalogowanego usera.
            </div>
        )
    } else {
        AccountData = (
            <div>
                Brak danych
            </div>
        )
    }

    if (AccountType === "indywidualne") {
        AccFormData = (
            <div className="AccountInfo">
                <h3>DANE KONTA</h3>
                <div className="AccountMainData">
                    <p><label htmlFor="Login">Login</label>
                    <input type="text"></input></p>
                    <p>E-mail: {User.accinfo.mail}</p>
                </div>
                <hr className="separator"/>
                <h3>DANE PERSONALNE</h3>
                <div>
                    <p>
                        <label htmlFor="Imię">Imię</label>
                        <input type="text"></input>
                    </p>
                    <p>
                        <label htmlFor="Nazwisko">Nazwisko</label>
                        <input type="text"></input>
                    </p>
                </div>
                <hr className="separator"/>
                <h3>ADRES</h3>
                <div className="addressline">
                    <div>
                        <input type="text" name="street"></input><br/>
                        <label htmlFor="street">Ulica</label>
                    </div>
                    <div>
                        <input type="text" name="streetno"></input><br/>
                        <label htmlFor="streetno">Nr domu</label>
                    </div>
                    <div>
                        <input type="text" name="addressno" className="addressno"></input><br/>
                        <label htmlFor="streetno">Nr lokalu</label>
                    </div>
                </div>
                <div className="addressline">
                    <div>
                        <input type="text" className="zipcode"></input> - <input type="text" className="zipcode"></input><br/>
                        <label htmlFor="zipcode">Kod pocztowy</label>
                    </div>
                    <div>
                        <input type="text"></input><br/>
                        <label htmlFor="streetno">Miasto</label>
                    </div>
                </div>
                <hr className="separator"/>
            </div>
        )
    }
    if (AccountType === "firmowe") {
        AccFormData = (
            <div className="AccountInfo">
                <h3>DANE KONTA</h3>
                <div className="AccountMainData">
                    <p><label htmlFor="Login">Login</label>
                    <input type="text"></input></p>
                    <p>E-mail: {User.accinfo.mail}</p>
                </div>
                <hr className="separator"/>
                <h3>DANE FIRMY</h3>
                <div>
                    <p>
                        <label htmlFor="Imię">Nazwa firmy</label>
                        <input type="text" defaultValue={User.accinfo.cname}></input>
                    </p>
                    <p>
                        <label htmlFor="Imię">NIP</label>
                        <input type="text" defaultValue={User.accinfo.cnip}></input>
                    </p>
                    <p>
                        <label htmlFor="Imię">REGON</label>
                        <input type="text" defaultValue={User.accinfo.cregon}></input>
                    </p>
                </div>
                <hr className="separator"/>
                <h3>ADRES</h3>
                <div className="addressline">
                    <div>
                        <input type="text" name="street"></input><br/>
                        <label htmlFor="street">Ulica</label>
                    </div>
                    <div>
                        <input type="text" name="streetno" defaultValue={User.accinfo.chouse}></input><br/>
                        <label htmlFor="streetno">Nr domu</label>
                    </div>
                    <div>
                        <input type="text" name="addressno" className="addressno"></input><br/>
                        <label htmlFor="streetno">Nr lokalu</label>
                    </div>
                </div>
                <div className="addressline">
                    <div>
                        <input type="text" pattern="[0-9]*" className="zipcode"></input> - <input type="text" pattern="[0-9]*" className="zipcode"></input><br/>
                        <label htmlFor="zipcode">Kod pocztowy</label>
                    </div>
                    <div>
                        <input type="text"></input><br/>
                        <label htmlFor="streetno">Miasto</label>
                    </div>
                </div>
                <hr className="separator"/>
            </div>
        )
    }

    let ACStyle, EAStyle, CPStyle;
    //ACStyle = {display: 'block'};
    EAStyle = {display: 'none'};
    CPStyle = {display: 'none'};

    if (ACDisp === false) {
        ACStyle = {display: 'none'};
        EAStyle = {display: 'block'};
    }

    const changeTab = (option) => {
        if (option === 0) {
            console.log(0);
            setACDisp(false);
            setEADisp(true);
            setCPDisp(false);
            //ACStyle = {display: 'none', visibility: 'hidden', color: 'red'};
            //EAStyle = {display: 'block'};
            //CPStyle = {display: 'none'};
        }
    }

    return(
        <div className="SiteContainer">
            <TitleBar title="Konto użytkownika"/>
            <div className="AccountData" style={ACStyle}>
                <div className="AccountNameContainer">
                    <div className="AccountName">
                        <FaRegUserCircle size={50}/>
                        <h1>{fullname}</h1>
                    </div>
                    <div className="AccountType">Konto {AccountType}</div>
                </div>
                <ShowAccountData
                    login={User.userinfo.login}
                    mail={User.accinfo.mail}
                    iname={User.accinfo.iname}
                    isurname={User.accinfo.isurname}
                    address={address}
                    izip={User.accinfo.izip}
                    icity={User.accinfo.icity}
                    cname={User.accinfo.cname}
                    cnip={User.accinfo.cnip}
                    cregon={User.accinfo.cregon}
                    czip={User.accinfo.czip}
                    ccity={User.accinfo.ccity}
                    changetab={changeTab.bind(this, 0)}
                />
            </div>
            <div className="AccountData" style={ACStyle}>
                <div className="AccountNameContainer">
                    <div className="AccountName">
                        <FaRegUserCircle size={50}/>
                        <h1>{fullname}</h1>
                    </div>
                    <div className="AccountType">Konto {AccountType}</div>
                </div>
                <EditAccountData
                    login={User.userinfo.login}
                    mail={User.accinfo.mail}
                    iname={User.accinfo.iname}
                    isurname={User.accinfo.isurname}
                    address={address}
                    izip={User.accinfo.izip}
                    icity={User.accinfo.icity}
                    cname={User.accinfo.cname}
                    cnip={User.accinfo.cnip}
                    cregon={User.accinfo.cregon}
                    czip={User.accinfo.czip}
                    ccity={User.accinfo.ccity}
                    cstreet={User.accinfo.cstreet}
                    chouse={User.accinfo.chouse}
                    cflat={User.accinfo.cflat}
                />
            </div>
        </div>
    )
}

export default Account;

