import React, { useState, useEffect, useContext } from 'react';
import { useHistory } from 'react-router-dom';
import Axios from 'axios';
import { ServerPath, UserContext } from '../App';
import './UserOrders.scss';
import Titlebar from '../../components/TitleBar/TitleBar';

const UserOrders = (props) => {
    const [OrderList, setOrderList] = useState([]);
    const [User, setUser] = useContext(UserContext);

    const History = useHistory();    
    let OrderData;

    useEffect(() => {
        console.log(User.userinfo.uid);
        if (typeof(User.userinfo.uid) === 'undefined') {
            History.push('/');
        } else {
            Axios.post(ServerPath + 'ShowOrders.php', User.userinfo.uid)
            .then(res => {
                console.log(res.data);
                setOrderList(res.data);
            })
        }
    }, [])

    OrderData = (
        <div>
            {OrderList.map((r, index) => {
                return(
                    <p>{r.o_number}</p>
                )
            })}
        </div>
    )

    return(
        <div className="Order">
            <Titlebar title="Historia zamówień"/>
            {OrderData}
        </div>
    )
}

export default UserOrders;
